mapper.update:
UPDATE hotel_inventory_status
SET chain_id = #{chainId}, hotel_code = #{hotelCode}, stay_date = #{stayDate}, is_available = #{isAvailable}, remaining_inventory = #{remainingInventory}, sold_inventory = #{soldInventory}, physical_inventory = #{physicalInventory}
WHERE hotel_status_id = #{hotelStatusId}

mapper.select:
SELECT 
FROM hotel_inventory_status
WHERE hotel_id = #{hotelId}
AND stay_date >= #{startDate}
AND stay_date <= #{endDate}
ORDER BY stay_date ASC

SELECT 
FROM hotel_inventory_status
WHERE chain_id = #{chainId} 
  AND hotel_id = #{hotelId} 
  AND stay_date = #{stayDate}

mapper.update:
UPDATE hotel_inventory_status
SET is_available = #{isAvailable},
    remaining_inventory = #{remainingInventory},
    sold_inventory = #{soldInventory},
    physical_inventory = #{physicalInventory}
WHERE chain_id = #{chainId} 
  AND hotel_id = #{hotelId} 
  AND stay_date = #{stayDate}

mapper.select:
SELECT 
FROM hotel_inventory_status
WHERE hotel_id = #{hotelId} 
  AND stay_date = #{stayDate}
LIMIT 1

mapper.update:
UPDATE hotel_inventory_status
SET is_available = #{isAvailable}
WHERE hotel_id = #{hotelId} 
