mapper.update:
UPDATE room_types
room_type_name = #{roomTypeName}, description = #{description}, standard_price = #{standardPrice}, max_occupancy = #{maxOccupancy}, room_type_code = #{roomTypeCode}, physical_inventory = #{physicalInventory}, updated_at = NOW()
WHERE room_type_id = #{roomTypeId}

mapper.select:
SELECT 
    rt.room_type_id, rt.chain_id, rt.hotel_id, h.hotel_name, rt.room_type_name, rt.room_type_code, rt.description, 
    rt.standard_price, rt.max_occupancy, rt.created_at
FROM room_types rt
LEFT JOIN hotels h ON rt.hotel_id = h.hotel_id

    AND rt.hotel_id = #{hotelId}
    AND rt.chain_id = #{chainId}
    AND (rt.room_type_name LIKE CONCAT('%', #{roomTypeName}, '%') OR rt.room_type_code LIKE CONCAT('%', #{roomTypeName}, '%'))
    AND rt.room_type_code = #{roomTypeCode}

ORDER BY rt.created_at DESC

    LIMIT #{pageSize} OFFSET #{current}

SELECT rt.room_type_id, rt.chain_id, rt.hotel_id, h.hotel_name, rt.room_type_name, rt.room_type_code, rt.description, rt.standard_price, rt.max_occupancy, rt.created_at FROM room_types rt LEFT JOIN hotels h ON rt.hotel_id = h.hotel_id
AND rt.hotel_id = #{hotelId}
ORDER BY rt.created_at DESC

SELECT 
    rt.room_type_id, rt.chain_id, rt.hotel_id, h.hotel_name, rt.room_type_name, rt.room_type_code, rt.description, 
    rt.standard_price, rt.max_occupancy, rt.physical_inventory, rt.created_at
FROM room_types rt
LEFT JOIN hotels h ON rt.hotel_id = h.hotel_id
WHERE rt.room_type_code = #{roomTypeCode}
AND rt.hotel_id = #{hotelId}
AND rt.chain_id = #{chainId}

SELECT COUNT(*)
FROM room_types rt
LEFT JOIN hotels h ON rt.hotel_id = h.hotel_id

    AND rt.hotel_id = #{hotelId}
    AND rt.chain_id = #{chainId}
    AND (rt.room_type_name LIKE CONCAT('%', #{roomTypeName}, '%') OR rt.room_type_code LIKE CONCAT('%', #{roomTypeName}, '%'))
    AND rt.room_type_code = #{roomTypeCode}

SELECT * FROM room_types 
WHERE chain_id = #{chainId} 
AND hotel_id = #{hotelId}
AND room_type_code = #{roomTypeCode}
